let mongoose = require('mongoose')

let userSchema = new mongoose.Schema({
    _id: mongoose.Schema.Types.ObjectId,
    login: String,
    name: String,
    secondName: String,
    group: String,
    email: String,
    phone: String,
    pass: { 
        type: mongoose.Schema.Types.ObjectId, 
        ref: 'Passwords'
    },
    accessType: { 
        type: mongoose.Schema.Types.ObjectId, 
        ref: 'AccessType'
    }
  });
module.exports = mongoose.model('Users', userSchema)